{
    // python环境配置
    "python.condaPath": "/Users/yindahu/opt/anaconda3",
    // git
    "git.confirmSync": false,
    // 终端,启用内嵌终端
    "terminal.integrated.inheritEnv": false,
    // 设置python语言服务，提示，补全，高亮等
    "python.languageServer": "Pylance",
    "python.analysis.autoImportCompletions": true,
    "python.analysis.autoFormatStrings": true,
    "python.analysis.inlayHints.variableTypes": true,
    "python.analysis.inlayHints.pytestParameters": true,
    "python.analysis.inlayHints.functionReturnTypes": true,
    // 设置代码格式化,提前pip install yapf
    "[python]": {
        "editor.formatOnSave": false,
        "editor.defaultFormatter": "eeyore.yapf"
      },
    "yapf.args": [
      "--style",
      "{based_on_style: pep8, column_limit: 100, indent_width: 4, align_closing_bracket_with_visual_indent:true, allow_split_before_dict_value:false,  continuation_indent_width:4, split_arguments_when_comma_terminated:true, split_before_expression_after_opening_paren:false}"
    ]
}
